import React, {useEffect, useState} from 'react'
import {BrowserRouter, Switch, Route, Link} from 'react-router-dom';
import Button from '../components/Button';
import Item from '../components/Item';
import '../styles/Homepage.css'
import ItemPage from '../screens/ItemPage'

function Homepage ({ grabURL }) {

    const URL = "http://localhost:3001"

    const [items, setItems] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    const [isItemSelected, setIsItemSelected] = useState(false);
    const electronicURL = `${URL}/buyer/electronic`
    // const url = "http://localhost:3001/buyer/electronic/"

    useEffect(() => {
        async function fetchItems() {
            const resp = await fetch(electronicURL);
            const data = await resp.json();
            console.log(data.allElectronic);
            setItems(data.allElectronic);
            setIsLoaded(true)
        };
        fetchItems();
    },[])




    const itemList = items.map((item, index) => 
        <React.Fragment>
        <Link to={{
            pathname:"/store",
            search: `?${item.Name}=${item._id}`
        }}>
            <Item 
            key={index}
            id={item._id}
            name={item.Name}
            description={item.Description}
            url={`${electronicURL}/${item._id}`}
            />
        </Link>
        </React.Fragment>
    )

    return (
        <React.Fragment>
            <div className="login-button">
            <Link to='/buyer'>
                <Button name={'Buyer'}/>
            </Link>
            <Link to='/seller'>
                <Button name={'Seller'}/>
            </Link>
            </div>
            {!isItemSelected && <div className="itemContainer">
                {itemList}
            </div>}
            {/* {isItemSelected && } */}
        </React.Fragment>
    )
}

export default Homepage
